package org.jblooming.utilities;public class CodeValue {  public String code;  public String value;  public CodeValue(String cod, String val) {    code = cod;    value = val;  }  public boolean equals(Object obj) {    CodeValue cv = (CodeValue) obj;    return (code.equals(cv.code) && value.equals(cv.value));  }  public int hashCode() {    return code.hashCode() + value.hashCode();  }  public String toString() {    return code + " - "+value;  }}